import React, { Component } from "react";
import "./Header.css";
import "../CommonStyle.css"
import { Container, Alert, Button } from "react-bootstrap";
import ROSLIB from "roslib";

class Header extends Component {
  constructor(props) {
    super(props);
    this.ros = props.ros || null;
    this.state = {
      value: '',
      isConnected: false,
    };
    this.connectCb = props.connectCb;
    this.disconnectCb = props.disconnectCb;
    this.url = "ws://localhost:9090";
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.setGoal = this.setGoal.bind(this);
    this.getStations = this.getStations.bind(this);
  }

  handleChange(event) {
    this.state.value = event.target.value;
  }

  handleSubmit(event) {
    if (!this.state.isConnected) {
      if (this.state.value === '') {
        this.connectCb(this.url);
      } else {
        this.connectCb(this.state.value);
      }
    } else {
      this.disconnectCb();
    }
    event.preventDefault();
  }

  setGoal() {
    var actionClient = new ROSLIB.ActionClient({
      ros : this.props.ros,
      serverName : '/move_base',
      actionName : 'move_base_msgs/MoveBaseAction'
    });

    var positionVec3 = new ROSLIB.Vector3(null);
    var orientation = new ROSLIB.Quaternion({x:0, y:0, z:0, w:1.0});

    positionVec3.x = 3.0;
    positionVec3.y = 0.0;

    var pose = new ROSLIB.Pose({
      position : positionVec3,
      orientation : orientation
    });

    var goal = new ROSLIB.Goal({
      actionClient : actionClient,
      goalMessage : {
        target_pose : {
          header : {
            frame_id : 'map'
          },
          pose : pose
        }
      }
    });

    console.log("send goal");
    goal.send();
  }

  getStations() {
    var getStationsClient = new ROSLIB.Service({
      ros : this.ros,
      name : '/get_station_list',
      serviceType : 'kobuki_ui/GetStationList'
    });
    var request = new ROSLIB.ServiceRequest({});

    getStationsClient.callService(request, function(result) {
      console.log('Result for service call on '+ getStationsClient.name + ': '+ result.station_list);
    }, function(error) {
      console.log(error);
    });
  }

  componentWillReceiveProps(props) {
    this.setState({ isConnected: props.isConnected })
    this.ros = this.props.ros;
  }

  render() {
    return (
      <Container className="header-common w-100">
        <Alert variant={this.state.isConnected ? "success" : "danger"} style={{margin: "0"}}>
          {this.state.isConnected ? "Server is online !" : "Server is not available"}
        </Alert>
        <Button className="bg-primary" onClick={this.setGoal}>Set goal</Button>
        <Button className="bg-primary" onClick={this.getStations}>Get station</Button>
      </Container>
    )
  }
}

export default Header;
